def collectAccessionFeatureData(datafileName):   datafile = file(datafileName)   datafile_1 = file(datafileName)      filename = 'HBV RT accession features.txt'   featureFile = open(filename, 'w')   count_locus = 0   count_l = 0   for line in datafile:      if 'LOCUS' in line:         count_locus = count_locus + 1      elif 'PUBMED' in line:                  while count_l < count_locus:     # locate the RT variant in file            for line_1 in datafile_1:               if 'LOCUS' in line_1:                  count_l = count_l + 1                  break                                          # every RT variant has a unique (LOCUS, PUBMED, ACCESSION)         for line_1 in datafile_1:            if 'ACCESSION' in line_1:     # obtain the accession id (from datafile_1)               accessionID = ''               index = 12               C = line_1[index]               while C != '\n':                  accessionID = accessionID + C                  index = index + 1                  C = line_1[index]               break         for line in datafile:            if 'FEATURES' in line:        # obtain the isolate name, country & collection date                isolateName = ''           # (from datafile)               Country = ''               CollectionDate = ''               for line in datafile:                  if 'isolate=' in line:                     index = 31                     C = line[index]                     while C != '"':                        isolateName = isolateName + C                        index = index + 1                        C = line[index]                  elif 'country=' in line:                     index = 31                     C = line[index]                     while C != '"':                        Country = Country + C                        index = index + 1                        C = line[index]                  elif 'collection_date=' in line:                     index = 39                     C = line[index]                     while C != '"':                        CollectionDate = CollectionDate + C                        index = index + 1                        C = line[index]                     break                  elif 'ORIGIN' in line:                     break            elif '//' in line:               break         # record the accession features         record = accessionID + '##' + isolateName + '##' + Country + '##' + CollectionDate + '##' + '\n'         featureFile.write(record)   featureFile.close()if __name__ == "__main__":       collectAccessionFeatureData('HBV RT sequences.txt')