def collectData(datafileName):   datafile = file(datafileName)   datafile_1 = file(datafileName)      filename = 'HBV RT score info.txt'   scoreFile = open(filename, 'w')   count_locus = 0   count_l = 0   for line in datafile:      if 'LOCUS' in line:         count_locus = count_locus + 1      elif 'PUBMED' in line:         while count_l < count_locus:            for line_1 in datafile_1:               if 'LOCUS' in line_1:                  count_l = count_l + 1                  break         for line_1 in datafile_1:            if 'ACCESSION' in line_1:     # obtain the accession id               accessionID = ''               index = 12               C = line_1[index]               while C != '\n':                  accessionID = accessionID + C                  index = index + 1                  C = line_1[index]               for line_1 in datafile_1:     # obtain the gene product name                  if 'product' in line_1:                     Gene = ''                     index = 31                     C = line_1[index]                     while C != '"':                        Gene = Gene + C                        index = index + 1                        C = line_1[index]                     break               break         # record the score info         record = accessionID + '##' + Gene + '##' + '##' + '\n'         scoreFile.write(record)   scoreFile.close()if __name__ == "__main__":       collectData('HBV RT sequences.txt')