def collectAAseqData(datafileName):   # retrieve the amino-acid sequence   datafile = file(datafileName)      filename = 'HBV RT AA sequences info.txt'   AAseqFile = open(filename, 'w')   count = 0   for line in datafile:      if 'PUBMED' in line:         AminoAcid_sequence = ''         for line in datafile:            if 'CDS' in line and '1032' in line:               for line in datafile:                  if 'translation=' in line:                     index = 35                     C = line[index]                     while C != '\n':                        AminoAcid_sequence = AminoAcid_sequence + C                        index = index + 1                        C = line[index]                     for i in range(0, 5):                        for line in datafile:                           index = 21                           C = line[index]                           while C != '\n':                              AminoAcid_sequence = AminoAcid_sequence + C                              index = index + 1                              C = line[index]                           break                     for line in datafile:                        index = 21                        C = line[index]                        while C != '"':                           AminoAcid_sequence = AminoAcid_sequence + C                           index = index + 1                           C = line[index]                        break                     break               break    # the amino acid sequence is fully retrieved            elif 'ORIGIN' in line:               break    # there is no amino acid sequence available for the HBV RT         # record the sequence         record = AminoAcid_sequence + '\n'         AAseqFile.write(record)   AAseqFile.close()if __name__ == "__main__":       collectAAseqData('HBV RT sequences.txt')